qss.hochkontrast_dunkel:
QWidget { background: #0f0f10; color: #f2f2f2; font-size: 14px; }
QGroupBox { border: 1px solid #3a3a3d; border-radius: 8px; margin-top: 10px; }
QGroupBox::title { subcontrol-origin: margin; left: 12px; padding: 0 6px; }
QLineEdit, QTextEdit, QPlainTextEdit, QComboBox, QSpinBox, QDoubleSpinBox { background: #17171a; border: 1px solid #4a4a4f; border-radius: 8px; padding: 8px; }
QPushButton { background: #1f6feb; border: 1px solid #1f6feb; border-radius: 10px; padding: 8px 12px; min-height: 38px; }
QPushButton:hover { background: #2b78ff; }
QPushButton:disabled { background: #333338; border-color: #333338; color: #9a9aa3; }
QTabWidget::pane { border: 1px solid #3a3a3d; border-radius: 10px; }
QTabBar::tab { background: #17171a; border: 1px solid #3a3a3d; padding: 8px 10px; border-top-left-radius: 10px; border-top-right-radius: 10px; margin-right: 4px; }
QTabBar::tab:selected { background: #0f0f10; border-bottom-color: #0f0f10; }
QListWidget { background: #17171a; border: 1px solid #3a3a3d; border-radius: 10px; }
QLabel { color: #f2f2f2; }
QLabel[role="hint"] { color: #888888; }
QLabel[role="muted"] { color: #9a9aa3; }

